export default function Todo (change, setChange){
    const getCurrentTodo = (todo) => {
        setChange(todo)
    }


        return(
            <>
                <div className={'todo'}>
                    <TodoForm
                    />
                    <TodoDescription
                        getCurrentTodo={getCurrentTodo}
                    />
                </div>

            </>
        )
}
